import { useEffect, useState } from 'react';
import { ArrowRightCircle, ArrowRightCircleFill } from "react-bootstrap-icons";
import Col from "react-bootstrap/Col";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import headerImg from "../assets/img/header-img.svg";

const Banner = () => {
    const [isHovered, setIsHovered] = useState(false);
    const handleClick = () => {
        console.log("Let's connect");
    };
    const [index, setIndex] = useState(0);
    const [render, setRender] = useState(false);
    const [invisible, setInvisible] = useState(false);
    const description = ["Software Engineer", "Full-Stack Developer", "University of Melbourne", "Melbourne Space Program"];
    const [text, setText] = useState('');
    const [delta, setDelta] = useState(50);
    const time = 1000;

    useEffect(() => {
        let ticker = setInterval(() => {
            interval();
        }, delta)

        return () => {clearInterval(ticker)};
    }, [text]);

    const interval = () => {
        let i = index % description.length;
        let renderText = description[i];
        let newText = render ? renderText.substring(0, text.length -1) : renderText.substring(0, text.length + 1);

        setText(newText);

        if (render) {
            setDelta(prevDelta => prevDelta/2);
        }

        if (!render && newText === renderText) {
            setRender(true);
            setDelta(time);

        }
        else if (render && newText === '') {
            setRender(false);
            setIndex(index +1);

            setDelta(50);

            setText('.');
        }
    }
    const [isVisible, setIsVisible] = useState(true);

    useEffect(() => {
        const timer = setTimeout(() => {
        setIsVisible(!isVisible);
        }, 500);

        return () => clearTimeout(timer);
    }, [isVisible]);

    return (
        <section className = "banner" id = "home">
            <Container>
                <Row className = "align-items-center">

                    <Col xs = {12} md = {6} xl = {7}>
                        <span className="tagline">Welcome to my Portfolio</span>
                        <h1> {"Itmam Khan Labib "}  </h1>
                        <h2>
                        <span>{text}</span>
                        <span >_</span>
                        </h2>
                        <p>Hi I am this this this, I do this this this, please insert text, or dont, idk lol man. I'm just tryna live womp womp</p>

                        <button
                            onMouseEnter={() => setIsHovered(true)}
                            onMouseLeave={() => setIsHovered(false)}
                            >
                            Let's connect
                            {isHovered ? <ArrowRightCircleFill size={25} /> : <ArrowRightCircle size={25} />}
                        </button>

                    </Col>
                    <Col xs = {12} md= {6} xl = {5}>
                        <img src = {headerImg} alt = "Header Img"/>
                    </Col>
                </Row>
            </Container>
        </section>
    )
}

export default Banner;
