import { useState } from "react";
import { Col, Container, Row } from "react-bootstrap";
import dragonImg from "../assets/img/dragoncraft-png-5690494.svg";

const Contact = () => {
    const formInitialDetails = {
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        message: ''
    }

    const [formDetails, setFormDetails] = useState(formInitialDetails);
    const [buttonText, setButtonText] = useState('Send');
    const [status, setStatus] = useState({});

    const onFormUpdate = (category, value) => {
        setFormDetails({
            ...formDetails,
            [category]: value
        })
    }
    const handleSubmit = () => {

    }
    return (
        <section className = "contact" id = "contact">
            <Container>
                <Row className = "align-items-center">


                    <Col size = {12} md = {6} >
                        <h2>Get In Touch</h2>
                        <form onSubmit = {handleSubmit}>
                            <Row>
                                <Col sm = {6} className = "px-1">
                                    <input type = "text" name = "firstName" value = {formDetails.firstName} placeholder= "First Name" onChange={(e => onFormUpdate('firstName', e.target.value))}/>
                                </Col>
                                <Col sm = {6} className = "px-1">
                                    <input type = "text" name = "secondName" value = {formDetails.lastName} placeholder= "Last Name" onChange={(e => onFormUpdate('lastName', e.target.value))}/>
                                </Col>
                                <Col sm = {6} className = "px-1">
                                    <input type = "text" name = "email" value = {formDetails.email} placeholder= "Email" onChange={(e => onFormUpdate('email', e.target.value))}/>
                                </Col>
                                <Col sm = {6} className = "px-1">
                                    <input type = "tel" name = "phone" value = {formDetails.phone} placeholder= "Phone No." onChange={(e => onFormUpdate('phone', e.target.value))}/>
                                </Col>
                                <Col>
                                    <textarea row = "6" name = "message" value = {formDetails.message} placeholder= "Message" onChange={(e => onFormUpdate('message', e.target.value))}/>
                                    <button type = "submit"><span>{buttonText}</span></button>
                                </Col>
                                {
                                    status.message &&
                                    <Col>
                                        <p className={status.success === false ? "danger" : "success"}>{status.message}</p>
                                    </Col>
                                }

                            </Row>


                        </form>
                    </Col>
                    <Col size = {12} md = {6}>
                        <img  src={dragonImg}/>
                    </Col>
                </Row>
            </Container>
        </section>
    )
}
export default Contact;
