import { useState } from 'react';
import { Col, Container, Row } from "react-bootstrap";
import headerImg from "../assets/img/header-img.svg";

const Banner = () => {
    const [isHovered, setIsHovered] = useState(false);
    const handleClick = () => {
        console.log("Let's connect");
    };

    return (
        <section className = "banner" id = "home">
            <Container>
                <Row className = "align-items-center">
                    <Col xs = {12} md = {6} xl = {7}>
                        <span className="tagline">Welcome to my Portfolio</span>
                        <h1> {"Itmam Khan Labib"}  </h1>
                        <h1><span className="wrap">web developer</span></h1>
                        <p>Hi I am this this this, I do this this this, please insert text, or dont, idk lol man. I'm just tryna live womp womp</p>
                        <button onClick={handleClick}
                            onMouseEnter={() => setIsHovered(true)}
                            onMouseLeave={() => setIsHovered(false)}
                            className={isHovered ? 'hovered' : ''}>Let's connect<ArrowRightCircle size = {25}/>
                        </button>
                    </Col>
                    <Col xs = {12} md= {6} xl = {5}>
                        <img src = {headerImg} alt = "Header Img"/>
                    </Col>
                </Row>
            </Container>
        </section>
    )
}

export default Banner;
